require 'sinatra'
require 'httparty'
require 'nokogiri'
require 'uri'

get '/' do 
    send_file 'index.html'
end


get '/lunch' do
    lunch = ["샐러디", "20층", "김밥카페", "순남시래기", "땡기면땡비어"]
    @photos = {
              "샐러디"        => "data:image/jpeg;base64,",
              "20층"          => "data:image/jpeg;base64,",
              "김밥카페"      => "",
              "순남시래기"    => "", 
              "땡기면땡비어"  => ""
    }
    @menu = lunch.sample #erb에 쓰일 변수는 @붙임
    #send_file 'lunch.html'    erb로 바꿀꺼야
    
    erb :lunch
    
end

get '/lotto' do
    @lotto = (1...45).to_a.sample(6)
    #"lotto 추천번호 : #{lotto}"   erb로 바꿀꺼야
    erb :lotto
end


get '/welcome' do
    "Welcome !"
end

get '/welcome/:name' do
    "welcome #{params[:name]}"
end

get '/cube/:num' do
    input = params[:num].to_i
    result = input ** 3
    "This result is #{result} "
end


get '/lol' do 
    erb :lol
end


 

get '/search' do
    
    uri = "http://www.op.gg/summoner/userName="
    @id = params[:userName]
    keyword = URI.encode(@id)
    response = HTTParty.get(uri+keyword) 
    text = Nokogiri::HTML(response.body) 
    @win = text.css('#SummonerLayoutContent > div.tabItem.Content.SummonerLayoutContent.summonerLayout-summary > div.SideContent > div.TierBox.Box > div.SummonerRatingMedium > div.TierRankInfo > div.TierInfo > span.WinLose > span.wins')
    @lose = text.css('#SummonerLayoutContent > div.tabItem.Content.SummonerLayoutContent.summonerLayout-summary > div.SideContent > div.TierBox.Box > div.SummonerRatingMedium > div.TierRankInfo > div.TierInfo > span.WinLose > span.losses') 
    
    erb :search
    
end